namespace HashSlinger.Shared.Models;

using System.ComponentModel.DataAnnotations;

/// <summary>An error event from an agent.</summary>
/// <autogeneratedoc />
public record AgentError
{
    /// <summary>Gets or sets the agent.</summary>
    /// <value>The agent.</value>
    /// <autogeneratedoc />
    public virtual Agent Agent { get; set; } = null!;

    /// <summary>Gets or sets the task.</summary>
    /// <value>The task.</value>
    /// <autogeneratedoc />
    public virtual Task? Task { get; set; }


    /// <summary>Gets or sets the chunk.</summary>
    /// <value>The chunk.</value>
    public virtual Chunk? Chunk { get; set; }

    /// <summary>Gets or sets the chunk identifier.</summary>
    /// <value>The chunk identifier.</value>
    /// <autogeneratedoc />
    public int? ChunkId { get; set; }

    /// <summary>Gets or sets the error.</summary>
    /// <value>The error.</value>
    /// <autogeneratedoc />
    [Required]
    public string Error { get; set; } = null!;

    /// <summary>Gets or sets the identifier.</summary>
    /// <value>The identifier.</value>
    /// <autogeneratedoc />
    public int Id { get; set; }


    /// <summary>Gets or sets the time.</summary>
    /// <value>The time.</value>
    /// <autogeneratedoc />
    public DateTime Time { get; set; }
}
